import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Root health check for deployment compatibility
app.get('/', (req, res, next) => {
  const acceptHeader = req.get('Accept') || '';

  // If this is clearly a browser request for HTML, let it pass through to frontend
  // Otherwise, respond with health check (for deployment health checks, curl, etc.)
  if (acceptHeader.includes('text/html')) {
    // This looks like a browser requesting the frontend app
    next();
  } else {
    // Respond with health check for all other requests (deployment checks, API calls, etc.)
    return res.status(200).json({ status: 'ok', timestamp: new Date().toISOString() });
  }
});

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  // Log API requests (keeping minimal logging)

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  const isProduction = process.env.NODE_ENV === "production" || process.env.REPLIT_DEPLOYMENT === "1";

  if (!isProduction && app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    log("Starting in production mode, serving static files");
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, requireAuth, requireAdmin, requireSuperAdmin } from "./auth";
import { createUserSchema, updateUserSchema, createEmailSchema, createDomainSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  setupAuth(app);

  // Health check endpoint for deployment health checks
  app.get('/api/health', (req, res) => {
    res.status(200).json({ status: 'ok', timestamp: new Date().toISOString() });
  });

  // Auth routes
  app.get('/api/user', requireAuth, async (req: any, res) => {
    try {
      const user = req.user;
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User management routes (Admin/Super Admin only)
  app.get('/api/users', requireAdmin, async (req: any, res) => {
    try {
      const { role, search } = req.query;
      let users;

      if (search) {
        users = await storage.searchUsers(search as string);
      } else {
        users = await storage.getUsersByRole(role as any);
      }

      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.get('/api/users/stats', requireAdmin, async (req: any, res) => {
    try {
      const stats = await storage.getUserStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching user stats:", error);
      res.status(500).json({ message: "Failed to fetch user stats" });
    }
  });

  app.post('/api/users', requireSuperAdmin, async (req: any, res) => {
    try {
      const currentUser = req.user;

      const userData = createUserSchema.parse(req.body);
      const newUser = await storage.createUser(userData, currentUser.id);
      res.status(201).json(newUser);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating user:", error);
      res.status(500).json({ message: "Failed to create user" });
    }
  });

  app.patch('/api/users/:id', requireAdmin, async (req: any, res) => {
    try {
      const currentUser = req.user;

      const userData = updateUserSchema.parse(req.body);
      const updatedUser = await storage.updateUser(req.params.id, userData, currentUser.id);

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json(updatedUser);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Email routes
  app.get('/api/emails', requireAuth, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const { folder = 'inbox' } = req.query;

      const emails = await storage.getEmailsForUser(userId, folder as string);
      res.json(emails);
    } catch (error) {
      console.error("Error fetching emails:", error);
      res.status(500).json({ message: "Failed to fetch emails" });
    }
  });

  app.post('/api/emails', requireAuth, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const emailData = createEmailSchema.parse(req.body);

      const email = await storage.createEmail(userId, emailData);
      res.status(201).json(email);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating email:", error);
      res.status(500).json({ message: "Failed to send email" });
    }
  });

  app.patch('/api/emails/:id/status', requireAuth, async (req: any, res) => {
    try {
      const { isRead, isStarred } = req.body;
      await storage.updateEmailStatus(req.params.id, isRead, isStarred);
      res.json({ message: "Email status updated" });
    } catch (error) {
      console.error("Error updating email status:", error);
      res.status(500).json({ message: "Failed to update email status" });
    }
  });

  app.patch('/api/emails/:id/folder', requireAuth, async (req: any, res) => {
    try {
      const { folder } = req.body;
      await storage.moveEmailToFolder(req.params.id, folder);
      res.json({ message: "Email moved" });
    } catch (error) {
      console.error("Error moving email:", error);
      res.status(500).json({ message: "Failed to move email" });
    }
  });

  // Domain management routes (Super Admin only)
  app.get('/api/domains', requireSuperAdmin, async (req: any, res) => {
    try {
      const domains = await storage.getDomains();
      res.json(domains);
    } catch (error) {
      console.error("Error fetching domains:", error);
      res.status(500).json({ message: "Failed to fetch domains" });
    }
  });

  app.post('/api/domains', requireSuperAdmin, async (req: any, res) => {
    try {
      const currentUser = req.user;
      const domainData = createDomainSchema.parse(req.body);
      const newDomain = await storage.createDomain(domainData, currentUser.id);
      res.status(201).json(newDomain);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating domain:", error);
      res.status(500).json({ message: "Failed to create domain" });
    }
  });

  app.patch('/api/domains/:id/status', requireSuperAdmin, async (req: any, res) => {
    try {
      const { isActive } = req.body;
      await storage.updateDomainStatus(req.params.id, isActive);
      res.json({ message: "Domain status updated" });
    } catch (error) {
      console.error("Error updating domain status:", error);
      res.status(500).json({ message: "Failed to update domain status" });
    }
  });

  // Audit logs route (Admin/Super Admin only)
  app.get('/api/audit-logs', requireAdmin, async (req: any, res) => {
    try {
      const logs = await storage.getAuditLogs();
      res.json(logs);
    } catch (error) {
      console.error("Error fetching audit logs:", error);
      res.status(500).json({ message: "Failed to fetch audit logs" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  domains,
  emails,
  auditLogs,
  type User,
  type CreateUser,
  type UpdateUser,
  type Email,
  type CreateEmail,
  type CreateDomain,
  type Domain,
  type AuditLog,
  type UserRole,
} from "@shared/schema";
import { db, pool } from "./db";
import { eq, desc, and, or, like, count } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations (traditional auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(userData: CreateUser, createdBy: string): Promise<User>;
  updateUser(id: string, userData: UpdateUser, updatedBy: string): Promise<User | undefined>;
  getUsersByRole(role?: UserRole): Promise<User[]>;
  searchUsers(query: string): Promise<User[]>;
  getUserStats(): Promise<{
    total: number;
    admins: number;
    clients: number;
    activeToday: number;
  }>;

  // Domain operations
  getDomains(): Promise<Domain[]>;
  createDomain(domainData: CreateDomain, createdBy: string): Promise<Domain>;
  updateDomainStatus(id: string, isActive: boolean): Promise<void>;

  // Email operations
  getEmailsForUser(userId: string, folder?: string): Promise<Email[]>;
  createEmail(userId: string, emailData: CreateEmail): Promise<Email>;
  updateEmailStatus(emailId: string, isRead?: boolean, isStarred?: boolean): Promise<void>;
  moveEmailToFolder(emailId: string, folder: string): Promise<void>;

  // Audit log operations
  logAction(userId: string, action: string, targetUserId?: string, details?: any): Promise<void>;
  getAuditLogs(limit?: number): Promise<AuditLog[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations (traditional auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  // Extended user operations
  async createUser(userData: CreateUser, createdBy: string): Promise<User> {
    const id = randomUUID();
    const { tempPassword, ...userDataWithoutPassword } = userData;
    const [user] = await db
      .insert(users)
      .values({
        id,
        ...userDataWithoutPassword,
        password: tempPassword, // In real app, hash this password
      })
      .returning();

    // Log the action
    await this.logAction(createdBy, 'CREATE_USER', user.id, {
      userEmail: userData.email,
      userRole: userData.role,
    });

    return user;
  }

  async updateUser(id: string, userData: UpdateUser, updatedBy: string): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({
        ...userData,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();

    if (user) {
      await this.logAction(updatedBy, 'UPDATE_USER', user.id, userData);
    }

    return user;
  }

  async getUsersByRole(role?: UserRole): Promise<User[]> {
    if (role) {
      return await db.select().from(users).where(eq(users.role, role)).orderBy(desc(users.createdAt));
    }
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async searchUsers(query: string): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(
        or(
          like(users.firstName, `%${query}%`),
          like(users.lastName, `%${query}%`),
          like(users.email, `%${query}%`)
        )
      )
      .orderBy(desc(users.createdAt));
  }

  async getUserStats(): Promise<{
    total: number;
    admins: number;
    clients: number;
    activeToday: number;
  }> {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [totalResult] = await db.select({ count: count() }).from(users);
    const [adminResult] = await db.select({ count: count() }).from(users).where(eq(users.role, 'admin'));
    const [clientResult] = await db.select({ count: count() }).from(users).where(eq(users.role, 'client'));
    const [activeTodayResult] = await db
      .select({ count: count() })
      .from(users)
      .where(and(eq(users.isActive, true), eq(users.lastLoginAt, today)));

    return {
      total: totalResult.count,
      admins: adminResult.count,
      clients: clientResult.count,
      activeToday: activeTodayResult.count,
    };
  }

  // Domain operations
  async getDomains(): Promise<Domain[]> {
    return await db.select().from(domains).orderBy(desc(domains.createdAt));
  }

  async createDomain(domainData: CreateDomain, createdBy: string): Promise<Domain> {
    const [domain] = await db
      .insert(domains)
      .values({
        ...domainData,
        createdBy,
      })
      .returning();

    // Log the action
    await this.logAction(createdBy, 'CREATE_DOMAIN', undefined, {
      domain: domainData.domain,
    });

    return domain;
  }

  async updateDomainStatus(id: string, isActive: boolean): Promise<void> {
    await db.update(domains).set({ isActive }).where(eq(domains.id, id));
  }

  // Email operations
  async getEmailsForUser(userId: string, folder = 'inbox'): Promise<Email[]> {
    return await db
      .select()
      .from(emails)
      .where(and(eq(emails.userId, userId), eq(emails.folder, folder as any)))
      .orderBy(desc(emails.createdAt));
  }

  async createEmail(userId: string, emailData: CreateEmail): Promise<Email> {
    const [email] = await db
      .insert(emails)
      .values({
        ...emailData,
        userId,
        sender: 'user@emailserver.com', // In real app, get from user profile
        folder: 'sent',
      })
      .returning();

    return email;
  }

  async updateEmailStatus(emailId: string, isRead?: boolean, isStarred?: boolean): Promise<void> {
    const updateData: any = {};
    if (isRead !== undefined) updateData.isRead = isRead;
    if (isStarred !== undefined) updateData.isStarred = isStarred;

    if (Object.keys(updateData).length > 0) {
      await db.update(emails).set(updateData).where(eq(emails.id, emailId));
    }
  }

  async moveEmailToFolder(emailId: string, folder: string): Promise<void> {
    await db.update(emails).set({ folder: folder as any }).where(eq(emails.id, emailId));
  }

  // Audit log operations
  async logAction(userId: string, action: string, targetUserId?: string, details?: any): Promise<void> {
    await db.insert(auditLogs).values({
      userId,
      action,
      targetUserId,
      details,
    });
  }

  async getAuditLogs(limit = 100): Promise<AuditLog[]> {
    return await db
      .select()
      .from(auditLogs)
      .orderBy(desc(auditLogs.createdAt))
      .limit(limit);
  }
}

export const storage = new DatabaseStorage();

