import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Mail, Server, Shield, Users } from "lucide-react";

export default function AuthPage() {
  const { user, isLoading } = useAuth();
  const { toast } = useToast();

  const [loginData, setLoginData] = useState({
    username: '',
    password: '',
  });

  // Redirect to home if already logged in
  if (!isLoading && user) {
    window.location.href = "/";
    return null;
  }

  const loginMutation = useMutation({
    mutationFn: async (credentials: typeof loginData) => {
      const response = await apiRequest('POST', '/api/login', credentials);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Login realizado com sucesso",
        description: "Bem-vindo ao EmailServer Pro!",
      });
      // Force page reload to refresh authentication state
      window.location.reload();
    },
    onError: (error) => {
      toast({
        title: "Erro no login",
        description: "Usuário ou senha incorretos.",
        variant: "destructive",
      });
    },
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (!loginData.username || !loginData.password) {
      toast({
        title: "Campos obrigatórios",
        description: "Preencha usuário e senha.",
        variant: "destructive",
      });
      return;
    }
    loginMutation.mutate(loginData);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Carregando...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex">
      {/* Left side - Login Form */}
      <div className="flex-1 flex items-center justify-center p-8">
        <div className="w-full max-w-md">
          <div className="text-center mb-8">
            <Mail className="mx-auto text-4xl text-primary mb-4 h-16 w-16" />
            <h1 className="text-3xl font-bold text-foreground mb-2">EmailServer Pro</h1>
            <p className="text-muted-foreground">Sistema Profissional de E-mail</p>
          </div>

          <Card className="p-6">
            <form onSubmit={handleLogin} className="space-y-4">
              <div>
                <Label htmlFor="username" className="text-sm font-medium">
                  Usuário
                </Label>
                <Input
                  id="username"
                  type="text"
                  value={loginData.username}
                  onChange={(e) => setLoginData(prev => ({ ...prev, username: e.target.value }))}
                  placeholder="Digite seu usuário"
                  data-testid="input-login-username"
                />
              </div>

              <div>
                <Label htmlFor="password" className="text-sm font-medium">
                  Senha
                </Label>
                <Input
                  id="password"
                  type="password"
                  value={loginData.password}
                  onChange={(e) => setLoginData(prev => ({ ...prev, password: e.target.value }))}
                  placeholder="Digite sua senha"
                  data-testid="input-login-password"
                />
              </div>

              <Button 
                type="submit" 
                className="w-full py-6 text-lg"
                disabled={loginMutation.isPending}
                data-testid="button-submit-login"
              >
