                              variant="ghost" 
                              size="sm" 
                              onClick={() => handleToggleActive(userData.id, userData.isActive)}
                              disabled={updateUserMutation.isPending}
                              data-testid={`button-deactivate-${userData.id}`}
                            >
                              <UserMinus className="h-4 w-4" />
                            </Button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>Desativar usuário - bloqueia acesso ao sistema</p>
                          </TooltipContent>
                        </Tooltip>
                      ) : (
                        <Tooltip>
                          <TooltipTrigger asChild>
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              onClick={() => handleToggleActive(userData.id, userData.isActive)}
                              disabled={updateUserMutation.isPending}
                              data-testid={`button-activate-${userData.id}`}
                            >
                              <UserCheck className="h-4 w-4 text-green-600" />
                            </Button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>Reativar usuário - permite acesso ao sistema</p>
                          </TooltipContent>
                        </Tooltip>
                      )}
                      {userData.role !== 'super_admin' && (
                        <Tooltip>
                          <TooltipTrigger asChild>
                            <Button variant="ghost" size="sm" data-testid={`button-delete-${userData.id}`}>
                              <Trash2 className="h-4 w-4 text-destructive" />
                            </Button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>Excluir usuário permanentemente</p>
                          </TooltipContent>
                        </Tooltip>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {users.length === 0 && (
          <div className="py-8 text-center">
            <p className="text-muted-foreground">Nenhum usuário encontrado.</p>
          </div>
        )}
      </div>

      <CreateUserModal 
        isOpen={createUserOpen}
        onClose={() => setCreateUserOpen(false)}
        currentUser={user}
      />
    </div>
  );
}

client/src/components/email/compose-moda.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Paperclip, Link, Smile, X } from "lucide-react";
import type { User } from "@shared/schema";

interface ComposeModalProps {
  isOpen: boolean;
  onClose: () => void;
  user: User;
}

export default function ComposeModal({ isOpen, onClose, user }: ComposeModalProps) {
  const [formData, setFormData] = useState({
    recipient: '',
    subject: '',
    body: '',
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const sendEmailMutation = useMutation({
    mutationFn: async (emailData: typeof formData) => {
      await apiRequest('POST', '/api/emails', emailData);
    },
    onSuccess: () => {
      toast({
        title: "Email enviado",
        description: "Seu email foi enviado com sucesso.",
      });
      setFormData({ recipient: '', subject: '', body: '' });
      onClose();
      queryClient.invalidateQueries({ queryKey: ['/api/emails'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erro ao enviar",
        description: "Não foi possível enviar o email. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.recipient || !formData.subject || !formData.body) {
      toast({
        title: "Campos obrigatórios",
        description: "Preencha todos os campos para enviar o email.",
        variant: "destructive",
      });
      return;
    }
    sendEmailMutation.mutate(formData);
  };

  const handleInputChange = (field: keyof typeof formData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleSaveDraft = () => {
    toast({
      title: "Rascunho salvo",
      description: "Seu email foi salvo nos rascunhos.",
    });
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-hidden">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            Nova Mensagem
            <Button variant="ghost" size="sm" onClick={onClose} data-testid="button-close-compose">
              <X className="h-4 w-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Input
              type="email"
              placeholder="Para"
              value={formData.recipient}
              onChange={(e) => handleInputChange('recipient', e.target.value)}
              data-testid="input-recipient"
            />
          </div>
          <div>
            <Input
              type="text"
              placeholder="Assunto"
              value={formData.subject}
              onChange={(e) => handleInputChange('subject', e.target.value)}
              data-testid="input-subject"
            />
          </div>
          <div>
            <Textarea
              rows={12}
              placeholder="Escreva sua mensagem..."
              value={formData.body}
              onChange={(e) => handleInputChange('body', e.target.value)}
              className="resize-none"
              data-testid="textarea-body"
            />
          </div>
        </form>

        <div className="flex items-center justify-between pt-4 border-t border-border">
          <div className="flex items-center space-x-2">
            <Button variant="ghost" size="sm" title="Anexar arquivo" data-testid="button-attach">
              <Paperclip className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm" title="Inserir link" data-testid="button-link">
              <Link className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm" title="Emoji" data-testid="button-emoji">
              <Smile className="h-4 w-4" />
            </Button>
          </div>
          <div className="flex items-center space-x-2">
            <Button 
              variant="outline" 
              onClick={handleSaveDraft}
              disabled={sendEmailMutation.isPending}
              data-testid="button-save-draft"
            >
              Salvar Rascunho
            </Button>
            <Button 
              type="submit" 
              onClick={handleSubmit}
              disabled={sendEmailMutation.isPending}
              data-testid="button-send"
            >
              {sendEmailMutation.isPending ? 'Enviando...' : 'Enviar'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/email/email-list.t.content:
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  RotateCcw, 
  Archive, 
  Trash2, 
  AlertTriangle,
  ChevronLeft,
  ChevronRight,
  Star,
  Inbox
} from "lucide-react";
import type { User, Email } from "@shared/schema";

interface EmailListProps {
  currentSection: string;
  user: User;
}

export default function EmailList({ currentSection, user }: EmailListProps) {
  const [selectedEmails, setSelectedEmails] = useState<string[]>([]);

  const { data: emails = [], isLoading } = useQuery<Email[]>({
    queryKey: ['/api/emails', { folder: currentSection }],
    retry: false,
  });

  const formatTime = (dateString: string | Date | null) => {
    if (!dateString) return '';
    const date = typeof dateString === 'string' ? new Date(dateString) : dateString;
    const now = new Date();
    const diffInHours = Math.abs(now.getTime() - date.getTime()) / 36e5;

    if (diffInHours < 24) {
      return date.toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' });
    }
    return date.toLocaleDateString('pt-BR', { day: '2-digit', month: '2-digit' });
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedEmails(emails.map((email: Email) => email.id));
    } else {
      setSelectedEmails([]);
    }
  };

  const handleSelectEmail = (emailId: string, checked: boolean) => {
    if (checked) {
      setSelectedEmails([...selectedEmails, emailId]);
    } else {
      setSelectedEmails(selectedEmails.filter(id => id !== emailId));
    }
  };

  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Carregando emails...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col">
      {/* Email List Header */}
      <div className="bg-card border-b border-border px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Checkbox 
              checked={selectedEmails.length === emails.length && emails.length > 0}
              onCheckedChange={handleSelectAll}
              data-testid="checkbox-select-all"
            />
            <div className="flex items-center space-x-2">
              <Button variant="ghost" size="sm" title="Atualizar" data-testid="button-refresh">
                <RotateCcw className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" title="Arquivar" data-testid="button-archive">
                <Archive className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" title="Excluir" data-testid="button-delete">
                <Trash2 className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" title="Marcar como spam" data-testid="button-spam">
                <AlertTriangle className="h-4 w-4" />
              </Button>
            </div>
          </div>
          <div className="flex items-center space-x-2 text-sm text-muted-foreground">
            <span data-testid="text-email-count">1-{emails.length} de {emails.length}</span>
            <Button variant="ghost" size="sm" data-testid="button-prev-page">
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm" data-testid="button-next-page">
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>

      {/* Email List */}
      <div className="flex-1 overflow-y-auto">
        {emails.length === 0 ? (
          <div className="flex items-center justify-center h-64">
            <div className="text-center">
              <Inbox className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-lg font-medium text-foreground">Nenhum email encontrado</p>
              <p className="text-muted-foreground">Sua {currentSection === 'inbox' ? 'caixa de entrada' : currentSection} está vazia.</p>
            </div>
          </div>
        ) : (
          emails.map((email: Email) => (
            <div 
              key={email.id} 
              className="email-item border-b border-border p-4 cursor-pointer"
              data-testid={`email-item-${email.id}`}
            >
              <div className="flex items-center space-x-4">
                <Checkbox 
                  checked={selectedEmails.includes(email.id)}
                  onCheckedChange={(checked) => handleSelectEmail(email.id, checked as boolean)}
                  data-testid={`checkbox-email-${email.id}`}
                />
                <Button 
                  variant="ghost" 
                  size="sm" 
                  className={email.isStarred ? "text-yellow-500" : "text-muted-foreground hover:text-yellow-500"}
                  data-testid={`button-star-${email.id}`}
                >
                  <Star className={`h-4 w-4 ${email.isStarred ? 'fill-current' : ''}`} />
                </Button>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3 min-w-0">
                      <span 
                        className={`${email.isRead ? 'text-muted-foreground' : 'font-medium text-foreground'}`}
                        data-testid={`text-sender-${email.id}`}
                      >
                        {email.sender}
                      </span>
                      <span 
                        className={`text-sm truncate ${email.isRead ? 'text-muted-foreground' : 'font-medium text-foreground'}`}
                        data-testid={`text-subject-${email.id}`}
                      >
                        {email.subject}
                      </span>
                    </div>
                    <span 
                      className="text-sm text-muted-foreground whitespace-nowrap"
                      data-testid={`text-time-${email.id}`}
                    >
                      {formatTime(email.createdAt!)}
                    </span>
                  </div>
                  <p 
                    className="text-sm text-muted-foreground truncate mt-1"
                    data-testid={`text-preview-${email.id}`}
                  >
                    {email.body.substring(0, 150)}...
                  </p>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

